</table><script language="javascript" type="text/javascript">
var countdownfrom=60;
var currentsecond=document.ShoutBox.autorefresh.value=countdownfrom+1;
function autoRefresh(){
if (currentsecond!=0){
currentsecond-=1;
document.ShoutBox.autorefresh.value=currentsecond;
}
else{
window.location.reload();
return;
}
AUTOREF = setTimeout("autoRefresh()",1000);
}
function stopRefresh() {
if (window.AUTOREF) clearTimeout(AUTOREF);
}
function startRefresh() {
autoRefresh();
}
